import { UserOutlined } from '@ant-design/icons';
import { Button, Form, Input, Flex, message} from 'antd';
import { Link } from 'react-router-dom';
import logoBookMyShow from "../../Assets/Bookmyshow-logoid.png";
import './Login.css'
import { forgotPassword } from '../../Api/Authentication';

function ForgotPassword(){
    const onFinish = async(values) => {
        const request = {email:values.email}

        const response = await forgotPasswordAPI(request);

        if (response.success){
            message.success(response.message);          
        }
        else{
            message.error(response.message);
        }
    };

    return <div className='Login'>
        <main>
            <section className='sectionHeading'>
                <h2 className="login-heading">Enter your
                    <span className="logo-text"><img className="inline-logo" src={logoBookMyShow} alt="BookMyShow Logo" /> Email</span>
                </h2>
            </section>
            <section>
                <Form
                    name="login"
                    initialValues={{ remember: true }}
                    style={{ maxWidth: 360 }}
                    onFinish={onFinish}
                    >
                    <Form.Item
                        name="email"
                        rules={[{ required: true, message: 'Please input your Email!' }]}
                    >
                        <Input prefix={<UserOutlined />} type="email" placeholder="Email" />
                    </Form.Item>
                    <Form.Item>
                        <Button block type="primary" htmlType="submit">
                            Forgot Password
                        </Button>
                        <Flex justify="center" align="center">Existing User ? 
                            <Link to="/">  Login here</Link>
                        </Flex>
                    </Form.Item> 
                </Form>
            </section>
        </main>
    </div>
}

export default ForgotPassword;
