import { useParams } from 'react-router-dom';
import AfterLogInNavbar from '../../Component/AfterLogInNavbar/AfterLoggedInNavbar';
import { useState, useEffect } from 'react';
import { fetchMovieById } from '../../Api/HomeMovies';

function MovieDetailsPage(){
    const [movie, setMovie] = useState();

    const params = useParams();
    const movieId = params.movieId;

    useEffect(() =>{
        fetchMovieDetails(movieId);
    })

    const fetchMovieDetails = async() =>{
        const movieDetails = await fetchMovieById();
        setMovie(movieDetails);
    }

    return <div>
        <AfterLogInNavbar/>
        <div className='MovieDetailsSection'>
            <div>
                <img className="MoviePoster" src={movie.poster} alt="moviePoster"></img>
            </div>

            <div>
                <h1>movie.name</h1>
                <h4></h4>
                <p> Release Date: </p>
                <p> Duration: </p>
                <p> </p>
            </div>

        </div>
    </div>

}

export default MovieDetailsPage;
