import { axiosInstance } from "./AxiosInstance";
 
export const createBooking = async(data) =>{
    try{
        const response = await axiosInstance.post("http://localhost:8888/bookings", data);
        return response.data;
    }catch(err){
        console.log(err);
        return err.response.data;
    }
}

export const makePayment = async(data) =>{
    try{
        const response = await axiosInstance.post("http://localhost:8888/payments", data);
        return response.data;
    }catch(err){
        console.log(err);
        return err.response.data;
    }
}

module.exports={
    createBooking,makePayment
}
