import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import './BookShow.css'
import { getShowDetails } from '../../Api/Shows';

function BookShow(){

    const [showDetails, setShowDetails] = useState(null);
    const [selectedSeats, setSelectedSeats] = useState([]);

    const params = useParams();
    const showId = params.showId;

    const fetchShowDetails = async()=>{
        const showResponse = await getShowDetails(showId);
        setShowDetails(showResponse.data);
    }
    useEffect(()=>{
        fetchShowDetails();
    },[]);

    const handleSelectedSeats = (seatNumber)=>{
        console.log(seatNumber);
    }

    
    const getSeats = ()=>{

        const totalSeats = showDetails ? showDetails.totalSeats: 0;

        const columns = 20;
        const rows = Math.ceil(totalSeats/columns);

        let allRows = [];
        for (let i=0; i<rows; i++){
            allRows.push(i);
        }

        let allColumns = [];
        for (let i=0; i<columns; i++){
            allColumns.push(i);
        }

        return <div>
            {
                allRows.map((row)=>{
                    return <div>
                        {
                            allColumns.map((column)=>{
                                let seatNumber = row * columns + column + 1;

                                let seatClass = "seat-btn"

                                const isSeatBooked = showDetails.bookedSeats.includes(seatNumber);
                                const isSeatSelected = selectedSeats.includes(seatNumber);

                                if (isSeatBooked){
                                    seatClass+= "booked";
                                }

                                if (isSeatSelected){
                                    seatClass += "selected";
                                }


                                return <button onClick={()=>handleSelectedSeats(seatNumber)} className={seatClass}>{seatNumber}</button>
                            })
                        }
                    </div> 
                })
            }
        </div>

    }

    return <div>
        {
            showDetails === null && <div className='loadingShowDetails'><h2>Fetching show view...</h2></div>
        }

        {
            showDetails && <div className='ShowHeaderContainer'>
                <div className='ShowHeader1'>
                    <div className='details'>
                        <h4 className='ShowMovieName'>{showDetails.movie.name}</h4>
                        <h4 className='ShowTheaterDetail'>
                            <span>{showDetails.theater.name}</span>: 
                            <span> {showDetails.theater.address}</span> | 
                            <span> {showDetails.showDate.split("T")[0]}</span>
                        </h4>
                    </div>

                    <div className='showAvailableSeats'>
                        {`Available seats: ${showDetails.totalSeats - showDetails.bookedSeats}`}
                    </div>
                </div>

                <div className='ShowHeader2'>
                    <div className='showtimeSec'>
                        {showDetails.showTime}
                    </div>
                </div>                
            </div>
        }
        <div className='seatSection'>
            {
                getSeats()
            }
        </div>
        
        
    </div>
}

export default BookShow;
