import { LockOutlined, UserOutlined } from '@ant-design/icons';
import { Button, Form, Input, message } from 'antd';
import './Login.css'
import BeforeLogInNavbar from '../../Component/BeforeLogInNavbar/BeforeLoggedInNavbar.js';
import Password from 'antd/es/input/Password.js';
import OTP from 'antd/es/input/OTP/index.js';


function ResetPassword(){
    const onFinish = async(values) => {
        const request = {
            OTP:values.otp,
            Password:values.Password
        }
        
        const response = await forgotPasswordAPI(request);
        console.log(response);
    };

    return <div>
    <BeforeLogInNavbar/>
        <div className='Login'>
            <main>
                <section className='sectionHeading'>
                    <h2 className="login-heading">Enter your OTP</h2>
                </section>
                <section >
                    <Form
                        name="Reset Password"
                        initialValues={{ remember: true }}
                        style={{ maxWidth: 360 }}
                        onFinish={onFinish}
                        >
                        <Form.Item
                            name="OTP"
                            rules={[{ required: true, message: 'Please input your OTP!' }]}
                        >
                            <Input type="number" placeholder="OTP" />
                        </Form.Item>

                        <Form.Item
                            name="New password"
                            rules={[{ required: true, message: 'Please input your new Password!' }]}
                        >
                            <Input prefix={<LockOutlined />} type="password" placeholder="New Password" />
                        </Form.Item>

                        <Form.Item>
                            <Button block type="primary" htmlType="submit">
                                Reset Password
                            </Button>
                        </Form.Item>
                    </Form>
                </section>
            </main>
        </div>
    </div>
}

export default ResetPassword;
